domains.vhostConfig:
# WebSocket support - Map for connection upgrade
map $http_upgrade $connection_upgrade {
    default upgrade;
    '' close;
}


upstream dvwa_local_backend {

    server dvwa:80 weight=1 max_fails=3 fail_timeout=30s;

    # Keepalive connections - 10 per backend (1 backends)
    keepalive 10;
}

server {
    listen 80;
    server_name dvwa.local;



    # Include ACL rules (IP whitelist/blacklist)
    include /etc/nginx/conf.d/acl-rules.conf;

    # Include ACME challenge location for Let's Encrypt
    include /etc/nginx/snippets/acme-challenge.conf;

    modsecurity on;

    access_log /var/log/nginx/dvwa.local_access.log main;
    error_log /var/log/nginx/dvwa.local_error.log warn;

    location / {
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;

        # WebSocket support
        proxy_set_header Upgrade $http_upgrade;
        proxy_set_header Connection $connection_upgrade;

        # WebSocket timeout settings
        proxy_read_timeout 86400s;
        proxy_send_timeout 86400s;
        proxy_pass http://dvwa_local_backend;




        # Health check settings
        proxy_next_upstream error timeout http_502 http_503 http_504;
        proxy_next_upstream_tries 3;
        proxy_next_upstream_timeout 5s;

    }

    location /nginx_health {
        access_log off;
        return 200 "healthy\n";
        add_header Content-Type text/plain;
    }
}

